import React, { useContext, useMemo } from 'react'
import styled, { ThemeContext } from 'styled-components'

export interface BoxProps {
  alignItems?: 'baseline' | 'center' | 'flex-end' | 'flex-start',
  children?: React.ReactNode,
  column?: boolean,
  flex?: number | string,
  height?: number | string,
  justifyContent?: 'center' | 'flex-end' | 'flex-start' | 'space-around' | 'space-between',
  margin?: number,
  marginBottom?: number,
  marginHorizontal?: number,
  marginLeft?: number,
  marginRight?: number,
  marginTop?: number,
  marginVertical?: number,
  maxHeight?: number | string,
  maxWidth?: number | string,
  minHeight?: number | string,
  minWidth?: number | string,
  overflow?: string,
  padding?: number,
  paddingBottom?: number,
  paddingHorizontal?: number,
  paddingLeft?: number,
  paddingRight?: number,
  paddingTop?: number,
  paddingVertical?: number,
  position?: 'relative' | 'absolute',
  reverse?: boolean,
  row?: boolean,
  width?: number | string
}

const Box: React.FC<BoxProps> = ({
  children,
  column,
  height,
  margin = 0,
  marginBottom = 0,
  marginHorizontal = 0,
  marginLeft = 0,
  marginRight = 0,
  marginTop = 0,
  marginVertical = 0,
  maxHeight,
  maxWidth,
  minHeight,
  minWidth,
  overflow,
  padding = 0,
  paddingBottom = 0,
  paddingHorizontal = 0,
  paddingLeft = 0,
  paddingRight = 0,
  paddingTop = 0,
  paddingVertical = 0,
  position,
  reverse,
  row,
  width,
  ...props
}) => {
  const { spacing } = useContext(ThemeContext)

  const display = useMemo(() => {
    if (row || column) {
      return 'flex'
    }
    return 'block'
  }, [column, row])

  const flexDirection = useMemo(() => {
    if (row && reverse) {
      return 'row-reverse'
    } if (column && reverse) {
      return 'column-reverse'
    } else if (column) {
      return 'column'
    }
    return undefined
  }, [column])

  const boxHeight = useMemo(() => {
    if (height) {
      return typeof height === 'number' ? height + 'px' : height
    }
    return undefined
  }, [height])

  const boxWidth = useMemo(() => {
    if (width) {
      return typeof width === 'number' ? width + 'px' : width
    }
    return undefined
  }, [width])

  const minBoxHeight = useMemo(() => {
    if (typeof minHeight !== 'undefined') {
      return typeof minHeight === 'number' ? minHeight + 'px' : minHeight
    }
    return minHeight
  }, [minHeight])

  const minBoxWidth = useMemo(() => {
    if (typeof minWidth !== 'undefined') {
      return typeof minWidth === 'number' ? minWidth + 'px' : minWidth
    }
    return minWidth
  }, [minWidth])

  const maxBoxHeight = useMemo(() => {
    if (maxHeight) {
      return typeof maxHeight === 'number' ? maxHeight + 'px' : maxHeight
    }
    return undefined
  }, [maxHeight])

  const maxBoxWidth = useMemo(() => {
    if (maxWidth) {
      return typeof maxWidth === 'number' ? maxWidth + 'px' : maxWidth
    }
    return undefined
  }, [maxWidth])

  return (
    <StyledBox
      {...props}
      display={display}
      flexDirection={flexDirection}
      height={boxHeight}
      margin={spacing[margin || 0]}
      marginBottom={spacing[marginBottom || marginVertical || 0]}
      marginLeft={spacing[marginLeft || marginHorizontal || 0]}
      marginRight={spacing[marginRight || marginHorizontal || 0]}
      marginTop={spacing[marginTop || marginVertical || 0]}
      maxHeight={maxBoxHeight}
      maxWidth={maxBoxWidth}
      minHeight={minBoxHeight}
      minWidth={minBoxWidth}
      overflow={overflow}
      padding={spacing[padding || 0]}
      paddingBottom={spacing[paddingBottom || paddingVertical || 0]}
      paddingLeft={spacing[paddingLeft || paddingHorizontal || 0]}
      paddingRight={spacing[paddingRight || paddingHorizontal || 0]}
      paddingTop={spacing[paddingTop || paddingVertical || 0]}
      position={position}
      width={boxWidth}
    >
      {children}
    </StyledBox>
  )
}

interface StyledBoxProps extends BoxProps {
  display: string,
  flexDirection?: string,
}

const StyledBox = styled.div<StyledBoxProps>`
  align-items: ${props => props.alignItems};
  display: ${props => props.display};
  flex: ${props => props.flex};
  flex-direction: ${props => props.flexDirection};
  height: ${props => props.height};
  justify-content: ${props => props.justifyContent};
  margin: ${props => props.margin ? props.margin + 'px' : undefined};
  margin-bottom: ${props => props.marginBottom ? props.marginBottom + 'px' : undefined};
  margin-left: ${props => props.marginLeft ? props.marginLeft + 'px' : undefined};
  margin-right: ${props => props.marginRight ? props.marginRight + 'px' : undefined};
  margin-top: ${props => props.marginTop ? props.marginTop + 'px' : undefined};
  max-height: ${props => props.maxHeight};
  max-width: ${props => props.maxWidth};
  min-height: ${props => props.minHeight};
  min-width: ${props => props.minWidth};
  overflow: ${props => props.overflow};
  padding: ${props => props.padding ? props.padding + 'px' : undefined};
  padding-bottom: ${props => props.paddingBottom ? props.paddingBottom + 'px' : undefined};
  padding-left: ${props => props.paddingLeft ? props.paddingLeft + 'px' : undefined};
  padding-right: ${props => props.paddingRight ? props.paddingRight + 'px' : undefined};
  padding-top: ${props => props.paddingTop ? props.paddingTop + 'px' : undefined};
  position: ${props => props.position};
  width: ${props => props.width};
`

export default Box

import { HSLA, Theme, ThemeConfig } from './types'

const DEFAULT_COLOR: HSLA = { h: 200, s: 100, l: 50 }

const createTheme = (config?: ThemeConfig): {
  dark: Theme,
  light: Theme
} => {
  const {
    baseColor = DEFAULT_COLOR,
    baseColorDark,
    baseGreyColor,
    baseGreyColorDark,
    borderRadius = 24,
    siteWidth = 1200
  } = config || {}

  const { h, s } = baseColor
  const { h: hDark, s: sDark } = baseColorDark || baseColor
  const { h: baseGreyH } = baseGreyColor || baseColor
  const { h: baseGreyDarkH } = baseGreyColorDark || baseColorDark || baseGreyColor || baseColor

  const blackHSLA: HSLA = { h: baseGreyH, s: 95, l: 4 }
  const whiteHSLA: HSLA = { h: baseGreyH, s: 100, l: 100 }
  const black = hslToCssString(blackHSLA)
  const white = hslToCssString(whiteHSLA)
  const grey = generateGreys(baseGreyH)

  const blackDarkHSLA: HSLA = { h: baseGreyDarkH, s: 95, l: 4 }
  const whiteDarkHSLA: HSLA = { h: baseGreyDarkH, s: 100, l: 100}
  const blackDark = hslToCssString(blackDarkHSLA)
  const whiteDark = hslToCssString(whiteDarkHSLA)
  const greyDark = generateGreys(baseGreyDarkH)


  const buttonSizes = {
    lg: 72,
    md: 48,
    sm: 36,
  }

  const colors = {
    black,
    grey,
    primary: {
      dark: hslToCssString({ h, s, l: 15 }),
      light: hslToCssString({ h, s, l: 75 }),
      main: hslToCssString(baseColor),
    },
    white,
  }

  const colorsDark = {
    black: blackDark,
    grey: greyDark,
    primary: {
      dark: hslToCssString({ h: hDark, s: sDark, l: 15 }),
      light: hslToCssString({ h: hDark, s: sDark, l: 75 }),
      main: hslToCssString(baseColorDark || baseColor)
    },
    white: whiteDark,
  }

  const spacing = {
    0: 0,
    1: 4,
    2: 8,
    3: 16,
    4: 24,
    5: 32,
    6: 48,
    7: 72,
    8: 96
  }

  const lightTheme: Theme = {
    baseBg: `radial-gradient(circle at top, ${grey[100]}, ${grey[200]})`,
    baseColor: grey[200],
    borderRadius,
    buttonSizes,
    colors,
    highlightColor: grey[100],
    shadowColor: grey[300],
    siteWidth,
    spacing,
    surfaces: generateSurfaces(
      {
        dark: grey[300],
        light: grey[100],
        main: grey[200],
      },
      hslToCssString({ ...whiteHSLA, a: 100 }),
      hslToCssString({ ...blackHSLA, a: 15 }),
    ),
    textColor: black,
  }

  const darkTheme: Theme = {
    ...lightTheme,
    baseBg: `radial-gradient(circle at top, ${greyDark[700]}, ${greyDark[800]})`,
    baseColor: greyDark[800],
    colors: colorsDark,
    highlightColor: greyDark[700],
    shadowColor: greyDark[900],
    surfaces: generateSurfaces(
      {
        dark: greyDark[900],
        light: greyDark[700],
        main: greyDark[800],
      },
      hslToCssString({ ...whiteDarkHSLA, a: 7.5 }),
      greyDark[900],
    ),
    textColor: whiteDark,
  }

  return {
    dark: darkTheme,
    light: lightTheme,
  }
}

const hslToCssString = (hsla: HSLA) => {
  const { h, s, l, a = 100 } = hsla
  return `hsl(${h}deg ${s}% ${l}% / ${a}%)`
}

const generateGreys = (h: number) => {
  return {
    100: hslToCssString({ h, s: 20, l: 96 }),
    200: hslToCssString({ h, s: 20, l: 94 }),
    300: hslToCssString({ h, s: 20, l: 90 }),
    400: hslToCssString({ h, s: 20, l: 70 }),
    500: hslToCssString({ h, s: 7, l: 52 }),
    600: hslToCssString({ h, s: 10, l: 37 }),
    700: hslToCssString({ h, s: 17, l: 15 }),
    800: hslToCssString({ h, s: 20, l: 10 }),
    900: hslToCssString({ h, s: 20, l: 5 }),
  }
}

const generateSurfaces = (
  base: {
    dark: string,
    light: string,
    main: string,
  },
  highlight: string,
  shadow: string,
) => {
  const baseBg = `radial-gradient(circle at top, ${base.light}, ${base.main})`
  return {
    N2: {
      background: `radial-gradient(circle at top, ${base.main}, ${base.dark})`,
      border: '0',
      highlight: `inset 4px -4px 8px -4px ${highlight}`,
      shadow: `inset -4px 4px 8px -4px ${shadow}`,
    },
    N1: {
      background: baseBg,
      border: '0',
      highlight: `inset 2px -2px 4px ${highlight}`,
      shadow: `inset -2px 2px 4px ${shadow}`,
    },
    0: {
      background: baseBg,
      border: `0`,
      highlight: `inset -1px 1px 0px ${highlight}`,
      shadow: `0px 0px 1px 1px ${shadow}`,
    },
    1: {
      background: baseBg,
      border: '0',
      highlight: `4px -4px 8px 0px ${highlight}`,
      shadow: `-4px 4px 8px 0 ${shadow}`,
    },
    2: {
      background: baseBg,
      border: '0',
      highlight: `8px -8px 16px 0px ${highlight}`,
      shadow: `-8px 8px 16px 0 ${shadow}`,
    }
  }
}

