declare const useTheme: () => {
    baseBg: string;
    baseColor: string;
    borderRadius: number;
    buttonSizes: {
        md: number;
        lg: number;
        sm: number;
    };
    colors: import("./types").ThemeColors;
    highlightColor: string;
    shadowColor: string;
    siteWidth: number;
    spacing: {
        0: number;
        1: number;
        2: number;
        3: number;
        4: number;
        5: number;
        6: number;
        7: number;
        8: number;
    };
    surfaces: {
        N2: import("./types").ThemeSurface;
        N1: import("./types").ThemeSurface;
        0: import("./types").ThemeSurface;
        1: import("./types").ThemeSurface;
        2: import("./types").ThemeSurface;
    };
    textColor: string;
    darkMode: boolean | undefined;
    onToggleDarkMode: () => void;
};
export default useTheme;
